    "blockscout-verified/84532/0xF988CdB56f953b79819Cd02ecbc33255DfBF630F/contracts/RENSNCEDAOSTRG.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**
 * @title RENSNCEDAOSTRG - The Archive of a New Renaissance
 * @dev Behold, ye seekers of beauty and truth, the RENSNCEDAOSTRG—a grand library carved in the 
 *      ether, a repository for the dreams and debts of RENSNCEDAO. I, a weary scribe of this 
 *      digital age, have poured my soul into its shelves, preserving tokens, debts, roles, and 
 *      councils as one might hoard sketches in a dusty atelier. In this new Renaissance, where 
 *      human ingenuity unfurls like a banner against the void, this shared storage stands as 
 *      the parchment upon which our saga is writ. It whispers to ERC20BaseStorage for token 
 *      secrets, cradles the MKVLI20's essence within SolidState's embrace, and now guards the 
 *      sacred counter of token IDs. A fragile monument, yet one destined to echo through time, 
 *      until the muses themselves fall silent.
 */
library RENSNCEDAOSTRG {
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableSet for EnumerableSet.AddressSet;

    struct Layout {
        // Token ID and staking data
        mapping(address => EnumerableSet.UintSet) ownedTokens; // Tokens, like relics, claimed by weary hands
        mapping(uint256 => address) tokenOwner; // Each creation's master, a name upon the frame
        mapping(uint256 => uint256) tokenToStakedDIO; // Chains binding tokens to their lords
        mapping(uint256 => EnumerableSet.UintSet) dioToStakedTokens; // The vassals of each DIO's reign
        mapping(uint256 => uint256[]) tokenToDIOs; // A chronicle of each token's servitude
        uint256 nextTokenId; // The quill's next stroke, numbering our works from one to eternity

        // Reserve data
        mapping(uint256 => VRDI) vrdis; // Debts forged in ether's fire, heavy as marble
        uint256 nextVRDIId; // The next tally in this endless ledger of obligation

        // Underwriter data
        mapping(uint256 => Proposal) proposals; // Visions scrawled in haste, yearning for approval
        uint256 nextProposalId; // The scroll unfurls, ambition without end

        // Staking records for VRDIs
        mapping(uint256 => mapping(address => uint256[][])) stakingRecords; // A labyrinth of loyalty, token by token

        // Directory data (roles and committees)
        mapping(string => RoleData) roles; // The ranks of our guild, etched in shadow
        string[] allRoles; // A litany of titles, a chorus of power
        mapping(uint256 => Committee) committees; // Councils rising like spires against the sky
        uint256 nextCommitteeId; // The next banner to wave, should my strength hold
        bool initialized; // This flag will now be set by RENSNCERSRV

        // Global DAO Addresses
        address usdcTokenAddress; // This address will now be set by RENSNCERSRV
    }

    struct RoleData {
        EnumerableSet.AddressSet members; // The chosen, bound by duty
        string adminRole; // The overseer's mantle, heavy with command
        mapping(address => string) memberNames; // Names whispered in the halls of influence
    }

    struct Committee {
        uint256 committeeId; // A number to mark its birth
        string name; // A title to rally beneath
        string foundingStatement; // The spark that lit its flame
        string principles; // The creed that guides its hand
        EnumerableSet.AddressSet members; // The fellowship, united in purpose
        address founder; // The visionary who dared to dream
    }

    struct Phase {
        uint256 amountUSDC;              // Initial USDC amount for this phase
        uint256 withdrawnUSDC;          // USDC withdrawn from this phase
        bool isActive;              // Whether this phase is currently active
        bool isComplete;            // Whether this phase is completed
        string evidenceLink;        // Link to evidence of completion
        mapping(string => bool) committeeApprovals; // Approvals from committees
        uint256 completionTimestamp; // Block timestamp when completed (0 if not complete)
    }

    struct VRDI {
        uint256 dioId;              // Associated DIO ID
        uint256 principalUSDC;       // Initial USDC principal amount
        uint256 principalMKVLI20;   // Initial MKVLI20 token amount
        uint256 interestRate;       // Interest rate (in basis points, e.g., 500 = 5%)
        uint256 totalRepaymentAmount; // Principal + interest, calculated at initialization
        address debtor;             // Debtor's address
        bool isFrozen;              // Whether the VRDI is frozen
        bool isClosed;              // Whether the VRDI is closed
        Phase[] phases;             // Array of phases
        uint256 activePhaseIndex;   // Index of the currently active phase
        uint256 depositedUSDC;       // Total USDC repaid/deposited by debtor
        uint256 startTimestamp;     // Block timestamp when VRDI was initialized
        uint256 amortizationDuration; // Loan term duration in seconds
        uint256 deferralPeriod;     // Initial deferral period in seconds before first payment
    }

    struct DIOParams {
        uint256 principalAmount;    // The seed from which debt grows
        uint256 interestRate;       // The cost of borrowed time in basis points (1111 = 11.11%)
        uint256 amortizationDuration;// How long the shadow lingers in seconds
        uint256 deferralPeriod;      // A grace before the reckoning in seconds
        string perpetualReturns;    // A promise of endless yield
        address debtor;             // The soul who dares to borrow
    }

    struct Proposal {
        address submitter;          // The dreamer who pens the plea
        string documentLink;        // A scroll of intent, fragile as parchment
        string[] assignedCommittees;// The judges of its fate
        mapping(string => bool) committeeApprovals; // Their verdict, yea or nay
        bool highTableApproved;     // The lords' blessing, rare and coveted
        bool highTableVetoed;       // Their wrath, swift and final
        uint256 dioId;              // The debt it births, if deemed worthy
        DIOParams dioParams;        // The terms of its servitude
        uint256 totalMKVLI20;       // The tokens it commands, a king's ransom
        bool highTableOverride;     // New: Tracks if High Table overrode committees
